    "nav.roadMarkings.regulatoryMarkings.longitudinalRegulatoryMarkings.question.0.text": {
        en: "What is the difference between these two road markings?",
        af: "Wat is die verskil tussen hierdie twee padmerke?",
        zu: "Uyini umehluko phakathi kwalezi zimaki zomgwaqo ezimbili?",
        xh: "Nguwuphi umahluko phakathi kweempawu zendlela ezimbini?",
    },
    "nav.roadMarkings.regulatoryMarkings.longitudinalRegulatoryMarkings.question.0.option.0.text": {
        en:
            "The marking on the left shows you may not pass other vehicles. The marking on the right ALSO shows that you may not drive over the line for any reason.",
        af:
            "Die merke links wys dat u nie ander voertuie mag verbygaan nie. Die merke aan die regterkant wys ook dat u om geen rede oor die lyn mag ry nie.",
        zu:
            "Ukumaka ngakwesobunxele kukhombisa ukuthi awukwazi ukudlula ezinye izimoto. Ukumakwa ngakwesokudla KUKHONA kukhombisa ukuthi kungenzeka ungashayeli ngaphezu komugqa nganoma yisiphi isizathu.",
        xh:
            "Ukumakishwa kwimiboniso ekhohlo awunakuze udlule kwezinye iimoto. Ukumakishwa ngasekunene KUPHELA kubonisa ukuba awunakuqhuba ngaphezulu komgca nangasiphi na isizathu.",
    },
    "nav.roadMarkings.regulatoryMarkings.longitudinalRegulatoryMarkings.question.0.option.1.text": {
        en: "There is no difference. They both show you may not pass other vehicles.",
        af: "Daar is geen verskil nie. Albei wys dat u nie ander voertuie mag verbygaan nie.",
        zu: "Akukho mehluko. Womabili akhombisa ukuthi awukwazi ukudlula ezinye izimoto.",
        xh: "Akukho mahluko. Zombini zikubonisa ukuba awunakudlula ezinye iimoto.",
    },
    "nav.roadMarkings.regulatoryMarkings.longitudinalRegulatoryMarkings.question.0.option.2.text": {
        en:
            "They both show you may not pass other vehicles. The one on the left is used on all roads and the one on the right on freeways only.",
        af:
            "Albei wys dat u nie ander voertuie mag verbygaan nie. Die een aan die linkerkant word op alle paaie gebruik en die een aan die regterkant op snelweë.",
        zu:
            "Womabili akhombisa ukuthi awukwazi ukudlula ezinye izimoto. Le ngakwesobunxele isetshenziswa kuyo yonke imigwaqo neyodwa ngakwesokudla emigwaqweni enguthelawayeka kuphela.",
        xh:
            "Zombini zikubonisa ukuba awunakudlula ezinye iimoto. Le ekhohlo isetyenziswa kuzo zonke iindlela kwaye enye ikwindlela yaseendlela kuphela.",
    },
